// n8n Code Node - Boring Business Opportunities Report Formatter
// This code formats the AI analysis output for email delivery

// Get the AI agent output from the previous node
const aiOutput = $input.all()[0].json.output || $input.all()[0].json.text || $input.all()[0].json;

// Extract the opportunity analysis content
let opportunityContent = '';
if (typeof aiOutput === 'object' && aiOutput.content) {
  opportunityContent = aiOutput.content;
} else if (typeof aiOutput === 'string') {
  // Clean up the content
  opportunityContent = aiOutput.replace(/^this is the output from the ai agent:\s*/i, '');
} else {
  opportunityContent = JSON.stringify(aiOutput);
}

// Clean up the content - remove \n literals and format properly
opportunityContent = opportunityContent
  .replace(/\\n/g, '\n')  // Convert literal \n to actual newlines
  .replace(/^\s+|\s+$/g, '')  // Trim whitespace
  .replace(/\n{3,}/g, '\n\n');  // Replace multiple newlines with double newlines

// Generate email subject with current date
function generateEmailSubject() {
  const currentDate = new Date().toLocaleDateString('en-US', { 
    year: 'numeric', 
    month: 'long', 
    day: 'numeric' 
  });
  return `ğŸ’¼ Boring Business Opportunities Report - ${currentDate}`;
}

